import React, { useEffect } from 'react';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import { SafeAreaProvider } from 'react-native-safe-area-context';
import { HomeScreen } from './src/screens/HomeScreen';
import { View, Text, LogBox, Platform } from 'react-native';
import Constants from 'expo-constants';

// Disable yellow box warnings in development
LogBox.ignoreAllLogs();

const Stack = createNativeStackNavigator();

class ErrorBoundary extends React.Component {
  state = { hasError: false, error: null };

  static getDerivedStateFromError(error) {
    console.error('getDerivedStateFromError:', error);
    return { hasError: true, error };
  }

  componentDidCatch(error, errorInfo) {
    console.error('App Error:', error);
    console.error('Error Info:', errorInfo);
    // Log the component stack
    if (errorInfo?.componentStack) {
      console.error('Component Stack:', errorInfo.componentStack);
    }
  }

  render() {
    if (this.state.hasError) {
      return (
        <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center', backgroundColor: '#fff' }}>
          <Text style={{ fontSize: 18, marginBottom: 10 }}>Something went wrong!</Text>
          <Text style={{ color: 'red', textAlign: 'center', padding: 20 }}>{this.state.error?.toString()}</Text>
        </View>
      );
    }
    return this.props.children;
  }
}

export default function App() {
  useEffect(() => {
    console.log('[App] Mounting application...');

    // Log the environment
    console.log('[App] Environment:', {
      platform: Platform.OS,
      version: Platform.Version,
      isExpo: Constants.expoVersion,
      appVersion: Constants.manifest?.version,
    });

    return () => console.log('[App] Application unmounted');
  }, []);

  return (
    <ErrorBoundary>
      <SafeAreaProvider>
        <View style={{ flex: 1, backgroundColor: '#fff' }}>
          <NavigationContainer
            onStateChange={(state) => console.log('[Navigation] State changed:', state)}
            onReady={() => console.log('[Navigation] Container ready')}
            fallback={<Text>Loading...</Text>}
          >
            <Stack.Navigator
              screenOptions={{
                headerShown: false,
              }}
            >
              <Stack.Screen 
                name="Home" 
                component={HomeScreen}
                listeners={{
                  focus: () => console.log('[Screen] Home screen focused'),
                  blur: () => console.log('[Screen] Home screen blurred'),
                }}
              />
            </Stack.Navigator>
          </NavigationContainer>
        </View>
      </SafeAreaProvider>
    </ErrorBoundary>
  );
}
